import { useQuery } from "@tanstack/react-query"
import { userApi, header } from '../api/api';
import { User } from "../interfaces/user";


const getListUser = async (): Promise<User[]> => {
    const { data } = await userApi.get<User[]>('/users');

    return data;
}

const saveUser = async (formData: FormData): Promise<User> => {
    const data = await userApi.post<User>(
        '/users',
        FormData,
        {
            headers: {
                'Content-Type': 'multipart/form-data',
            }
        }
    );

    return data.data;
}

const getUser = async ({id, formData}:{id:string, formData:FormData}): Promise<User> => {
    const { data } = await userApi.get<User>(`/users/${id}`);

    return data;
}

const updateUser = async (id: number): Promise<User> => {
    const { data } = await userApi.put<User>(`/users/${id}`, );

    return data;
}

const deleteUser = async (id: number): Promise<User> => {
    const { data } = await userApi.delete<User>(`/users/${id}`);

    return data;
}




export const useListUser = () => {
    const query = useQuery(
        ['listUser'],
        () => getListUser()
    );

    return { query };
}


export const useSaveUser = (formData:FormData) => {
    const query = useQuery(
        ['listUser'],
        () => saveUser(formData)
    );

    return { query };
}
