import { useForm, SubmitHandler } from "react-hook-form";

type Inputs = {
    id?: string,
    first_name?: string,
    second_name?: string,
    email?: string,
    avatar?: string
}

export const Form = () => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm<Inputs>();

    const onSubmit: SubmitHandler<Inputs> = data => console.log(data);

    // console.log(watch("userName")) // watch input value by passing the name of it

    return (
        // <form onSubmit={handleSubmit(onSubmit)}>

        //     <input defaultValue="test" {...register("example")} />

        //     <input {...register("exampleRequired", { required: true })} />


        //     {errors.exampleRequired && <span>This field is required</span>}


        //     <input type="submit" />
        // </form>

        <form className="row g-3" onSubmit={handleSubmit(onSubmit)} >
            <div className="col-md-12">
                <label className="form-label"><strong>First name</strong></label>
                <input
                    {...register("first_name",{required: true})}
                    type="text"
                    className={errors.first_name ? "form-control border-danger" : "form-control"}
                    placeholder="First name"
                />
                {errors.first_name && <small className="text-danger">This field is required</small>}
            </div>
            <div className="d-grid gap-2">
                <button className="btn btn-outline-success" type="submit">Save User</button>
                <button className="btn btn-outline-warning" type="submit">Update User</button>

            </div>
        </form>
    );
}
